import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { FiSend, FiPaperclip, FiSmile, FiUsers, FiSettings } from 'react-icons/fi';
import { format } from 'date-fns';

const SOCKET_URL = 'http://localhost:5000';

const EmojiPicker = ({ onEmojiClick, show }) => {
  const emojis = [
    { emoji: 'üòÄ', name: 'smile' },
    { emoji: 'üòÇ', name: 'laugh' },
    { emoji: 'üòç', name: 'heart_eyes' },
    { emoji: 'üò¢', name: 'sad' },
    { emoji: 'üò°', name: 'angry' },
    { emoji: 'üëç', name: 'thumbs_up' },
    { emoji: 'üëé', name: 'thumbs_down' },
    { emoji: '‚ù§Ô∏è', name: 'heart' },
    { emoji: 'üî•', name: 'fire' },
    { emoji: 'üíØ', name: 'hundred' }
  ];

  if (!show) return null;

  return (
    <div className="absolute bottom-12 right-0 bg-white border rounded-lg shadow-lg p-2 grid grid-cols-5 gap-1 z-50">
      {emojis.map((emoji) => (
        <button
          key={emoji.name}
          onClick={() => onEmojiClick(emoji.emoji)}
          className="p-2 hover:bg-gray-100 rounded text-lg"
        >
          {emoji.emoji}
        </button>
      ))}
    </div>
  );
};

const Message = ({ message, currentUser }) => {
  const isOwn = message.sender.id === currentUser?.id;

  const renderContent = () => {
    switch (message.type) {
      case 'file':
        return (
          <div className="flex items-center gap-2">
            <FiPaperclip className="text-blue-500" />
            <a
              href={`http://localhost:5000${message.fileUrl}`}
              download={message.content}
              className="text-blue-500 hover:underline"
            >
              {message.content}
            </a>
          </div>
        );
      case 'image':
        return (
          <div>
            <img 
              src={`http://localhost:5000${message.fileUrl}`} 
              alt={message.content}
              className="max-w-xs max-h-48 rounded-lg"
            />
            {message.content && <p className="mt-1">{message.content}</p>}
          </div>
        );
      default:
        return <p>{message.content}</p>;
    }
  };

  return (
    <div className={`flex ${isOwn ? 'justify-end' : 'justify-start'} mb-4`}>
      <div className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
        isOwn 
          ? 'bg-blue-500 text-white' 
          : 'bg-gray-200 text-gray-800'
      }`}>
        {!isOwn && (
          <p className="text-xs font-semibold mb-1 text-blue-600">
            {message.sender.username}
          </p>
        )}
        {renderContent()}
        <p className={`text-xs mt-1 ${isOwn ? 'text-blue-100' : 'text-gray-500'}`}>
          {format(new Date(message.timestamp), 'HH:mm')}
        </p>
      </div>
    </div>
  );
};

const TypingIndicator = ({ typingUsers }) => {
  if (typingUsers.length === 0) return null;

  return (
    <div className="flex items-center gap-2 px-4 py-2 text-gray-500 text-sm">
      <div className="flex gap-1">
        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
      </div>
      <span>
        {typingUsers.length === 1 
          ? `${typingUsers[0]} is typing...`
          : `${typingUsers.slice(0, -1).join(', ')} and ${typingUsers[typingUsers.length - 1]} are typing...`
        }
      </span>
    </div>
  );
};

const ChatApp = () => {
  const [socket, setSocket] = useState(null);
  const [connected, setConnected] = useState(false);
  const [user, setUser] = useState(null);
  const [username, setUsername] = useState('');
  const [currentRoom, setCurrentRoom] = useState('general');
  const [rooms, setRooms] = useState([]);
  const [messages, setMessages] = useState({});
  const [newMessage, setNewMessage] = useState('');
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [typingUsers, setTypingUsers] = useState([]);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);

  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);

  // Initialize socket connection
  useEffect(() => {
    console.log('Connecting to server...');
    const newSocket = io(SOCKET_URL);
    setSocket(newSocket);

    newSocket.on('connect', () => {
      console.log('Connected to server');
      setConnected(true);
    });

    newSocket.on('disconnect', () => {
      console.log('Disconnected from server');
      setConnected(false);
    });

    newSocket.on('authenticated', (data) => {
      console.log('Authenticated:', data);
      setUser(data.user);
    });

    newSocket.on('rooms_list', (roomsList) => {
      console.log('Rooms list:', roomsList);
      setRooms(roomsList);
    });

    newSocket.on('online_users', (users) => {
      console.log('Online users:', users);
      setOnlineUsers(users);
    });

    newSocket.on('room_messages', (data) => {
      console.log('Room messages:', data);
      setMessages(prev => ({
        ...prev,
        [data.room]: data.messages
      }));
    });

    newSocket.on('new_message', (data) => {
      console.log('New message:', data);
      setMessages(prev => ({
        ...prev,
        [data.room]: [...(prev[data.room] || []), data.message]
      }));
    });

    newSocket.on('user_typing', (data) => {
      if (data.roomId === currentRoom) {
        setTypingUsers(prev => [...prev.filter(u => u !== data.username), data.username]);
      }
    });

    newSocket.on('user_stopped_typing', (data) => {
      if (data.roomId === currentRoom) {
        setTypingUsers(prev => prev.filter(u => u !== data.username));
      }
    });

    return () => {
      console.log('Cleaning up socket');
      newSocket.close();
    };
  }, [currentRoom]);

  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, currentRoom]);

  const handleLogin = () => {
    console.log('Attempting login with username:', username);
    if (username.trim() && socket) {
      socket.emit('authenticate', { username: username.trim() });
    }
  };

  const handleSendMessage = () => {
    console.log('Sending message:', newMessage);
    if (newMessage.trim() && socket) {
      socket.emit('send_message', {
        roomId: currentRoom,
        content: newMessage.trim()
      });
      setNewMessage('');
      setShowEmojiPicker(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    console.log('Uploading file:', file.name);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData
      });

      if (response.ok) {
        const data = await response.json();
        console.log('File uploaded:', data);
        const isImage = file.type.startsWith('image/');

        if (socket) {
          socket.emit('send_message', {
            roomId: currentRoom,
            content: data.originalName,
            type: isImage ? 'image' : 'file',
            fileUrl: data.url
          });
        }
      }
    } catch (error) {
      console.error('File upload failed:', error);
    }

    e.target.value = '';
  };

  const handleEmojiClick = (emoji) => {
    setNewMessage(prev => prev + emoji);
    setShowEmojiPicker(false);
  };

  const getCurrentMessages = () => {
    return messages[currentRoom] || [];
  };

  // Loading screen
  if (!connected) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100">
        <div className="bg-white p-8 rounded-lg shadow-md">
          <h2 className="text-2xl font-bold mb-4">Connecting...</h2>
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
        </div>
      </div>
    );
  }

  // Login screen
  if (!user) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100">
        <div className="bg-white p-8 rounded-lg shadow-md w-96">
          <h2 className="text-2xl font-bold mb-4">Welcome to Chat App</h2>
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleLogin()}
            className="w-full p-3 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            onClick={handleLogin}
            className="w-full bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600 transition-colors"
          >
            Join Chat
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-80 bg-white shadow-md">
        {/* Header */}
        <div className="p-4 border-b border-gray-200">
          <h1 className="text-xl font-bold">Chat App</h1>
          <p className="text-sm text-gray-600">Welcome, {user.username}!</p>
        </div>

        {/* Rooms */}
        <div className="p-4 border-b border-gray-200">
          <h3 className="font-semibold text-gray-700 mb-2">Rooms</h3>
          <div className="space-y-1">
            {rooms.map((room) => (
              <button
                key={room.id}
                onClick={() => setCurrentRoom(room.id)}
                className={`w-full text-left p-3 rounded-lg transition-colors ${
                  currentRoom === room.id 
                    ? 'bg-blue-500 text-white' 
                    : 'hover:bg-gray-100'
                }`}
              >
                <div className="flex justify-between items-center">
                  <span># {room.name}</span>
                </div>
                <p className="text-xs text-gray-500 mt-1">
                  {room.users?.length || 0} members
                </p>
              </button>
            ))}
          </div>
        </div>

        {/* Online Users */}
        <div className="p-4">
          <h3 className="font-semibold text-gray-700 mb-2 flex items-center">
            <FiUsers className="mr-2" />
            Online Users ({onlineUsers.length})
          </h3>
          <div className="space-y-2 max-h-64 overflow-y-auto">
            {onlineUsers.map((onlineUser) => (
              <div
                key={onlineUser.id}
                className={`flex items-center p-2 rounded-lg ${
                  onlineUser.id === user.id ? 'bg-blue-50' : 'hover:bg-gray-100'
                }`}
              >
                <div className="w-3 h-3 bg-green-500 rounded-full mr-3"></div>
                <span className="text-sm">{onlineUser.username}</span>
                {onlineUser.id === user.id && (
                  <span className="text-xs text-gray-500 ml-2">(you)</span>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col">
        {/* Chat Header */}
        <div className="bg-white p-4 border-b border-gray-200 flex items-center justify-between">
          <div>
            <h2 className="text-lg font-semibold">
              {rooms.find(r => r.id === currentRoom)?.name || 'General'}
            </h2>
            <p className="text-sm text-gray-600">
              {rooms.find(r => r.id === currentRoom)?.users?.length || 0} members
            </p>
          </div>
          <div className="flex items-center gap-2">
            <button className="p-2 rounded-lg bg-gray-200 hover:bg-gray-300">
              <FiSettings />
            </button>
          </div>
        </div>

        {/* Messages */}
        <div className="flex-1 p-4 overflow-y-auto">
          {getCurrentMessages().map((message) => (
            <Message
              key={message.id}
              message={message}
              currentUser={user}
            />
          ))}
          <TypingIndicator typingUsers={typingUsers} />
          <div ref={messagesEndRef} />
        </div>

        {/* Message Input */}
        <div className="bg-white p-4 border-t border-gray-200">
          <div className="flex items-center gap-2">
            <input
              type="file"
              ref={fileInputRef}
              onChange={handleFileUpload}
              className="hidden"
            />
            <button
              onClick={() => fileInputRef.current?.click()}
              className="p-2 text-gray-500 hover:text-gray-700"
            >
              <FiPaperclip />
            </button>
            <div className="flex-1 relative">
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Type a message..."
                className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <div className="absolute right-2 top-2 flex items-center gap-1">
                <button
                  onClick={() => setShowEmojiPicker(!showEmojiPicker)}
                  className="p-1 text-gray-500 hover:text-gray-700"
                >
                  <FiSmile />
                </button>
                <EmojiPicker 
                  show={showEmojiPicker} 
                  onEmojiClick={handleEmojiClick}
                />
              </div>
            </div>
            <button
              onClick={handleSendMessage}
              className="bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600 transition-colors"
            >
              <FiSend />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatApp;
