import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { FiSend, FiPaperclip, FiSmile, FiUsers, FiSettings } from 'react-icons/fi';
import { format } from 'date-fns';

const SOCKET_URL = 'http://localhost:5000';

// Update the EmojiPicker component
const EmojiPicker = ({ onEmojiClick, show }) => {
  const emojis = [
    { emoji: 'üòÄ', name: 'grinning' },
    { emoji: 'üòÇ', name: 'joy' },
    { emoji: 'üòç', name: 'heart_eyes' },
    { emoji: 'ü§î', name: 'thinking' },
    { emoji: 'üò¢', name: 'sad' },
    { emoji: 'üò°', name: 'angry' },
    { emoji: 'üëç', name: 'thumbs_up' },
    { emoji: 'üëé', name: 'thumbs_down' },
    { emoji: '‚ù§Ô∏è', name: 'heart' },
    { emoji: 'üî•', name: 'fire' },
    { emoji: 'üíØ', name: 'hundred' },
    { emoji: 'üéâ', name: 'party' },
    { emoji: 'üò¥', name: 'sleepy' },
    { emoji: 'ü§ù', name: 'handshake' },
    { emoji: 'üëè', name: 'clap' },
    { emoji: 'üôå', name: 'raised_hands' },
    { emoji: 'ü§ó', name: 'hugs' },
    { emoji: 'üòä', name: 'blush' },
    { emoji: 'üéØ', name: 'target' },
    { emoji: '‚ö°', name: 'lightning' }
  ];

  if (!show) return null;

  return (
    <div className="emoji-picker absolute bottom-12 right-0 bg-white border rounded-lg shadow-xl p-3 grid grid-cols-5 gap-1 z-50 max-w-xs">
      {emojis.map((emoji) => (
        <button
          key={emoji.name}
          onClick={() => onEmojiClick(emoji.emoji)}
          className="emoji-picker-button p-2 hover:bg-gray-100 rounded-lg text-xl transition-all duration-200 flex items-center justify-center min-h-10 min-w-10"
          title={emoji.name}
        >
          <span className="emoji">{emoji.emoji}</span>
        </button>
      ))}
    </div>
  );
};

// Update the MessageReactions component
const MessageReactions = ({ reactions, onReact, messageId }) => {
  if (!reactions || Object.keys(reactions).length === 0) return null;

  return (
    <div className="flex flex-wrap gap-1 mt-2">
      {Object.entries(reactions).map(([emoji, users]) => (
        <button
          key={emoji}
          onClick={() => onReact(messageId, emoji)}
          className="message-reaction flex items-center gap-1 bg-gray-100 hover:bg-gray-200 rounded-full px-2 py-1 text-sm border transition-all duration-200"
          title={`${users.length} reaction${users.length > 1 ? 's' : ''}`}
        >
          <span className="emoji text-base">{emoji}</span>
          <span className="text-xs font-medium">{users.length}</span>
        </button>
      ))}
    </div>
  );
};

// Update the Message component's reaction picker
const Message = ({ message, currentUser, onReact, onFileDownload }) => {
  const [showReactions, setShowReactions] = useState(false);
  const isOwn = message.sender.id === currentUser?.id;

  const handleReact = (emoji) => {
    onReact(message.id, emoji);
    setShowReactions(false);
  };

  const quickReactions = ['üòÄ', 'üòÇ', 'üòç', 'üëç', 'üëé', '‚ù§Ô∏è', 'üî•', 'üíØ'];

  const renderContent = () => {
    switch (message.type) {
      case 'file':
        return (
          <div className="flex items-center gap-2">
            <FiPaperclip className="text-blue-500" />
            <button
              onClick={() => onFileDownload(message.fileUrl, message.content)}
              className="text-blue-500 hover:underline"
            >
              {message.content}
            </button>
          </div>
        );
      case 'image':
        return (
          <div>
            <img 
              src={`http://localhost:5000${message.fileUrl}`} 
              alt={message.content}
              className="max-w-xs max-h-48 rounded-lg"
            />
            {message.content && <p className="mt-1">{message.content}</p>}
          </div>
        );
      default:
        return <p className="break-words">{message.content}</p>;
    }
  };

  return (
    <div className={`flex ${isOwn ? 'justify-end' : 'justify-start'} mb-4`}>
      <div className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg relative ${
        isOwn 
          ? 'bg-blue-500 text-white' 
          : 'bg-gray-200 text-gray-800'
      }`}>
        {!isOwn && (
          <p className="text-xs font-semibold mb-1 text-blue-600">
            {message.sender.username}
          </p>
        )}
        {renderContent()}
        <div className="flex items-center justify-between mt-1">
          <p className={`text-xs ${isOwn ? 'text-blue-100' : 'text-gray-500'}`}>
            {format(new Date(message.timestamp), 'HH:mm')}
          </p>
          <div className="relative">
            <button
              onClick={() => setShowReactions(!showReactions)}
              className={`ml-2 p-1 rounded-full hover:bg-black hover:bg-opacity-10 transition-all ${
                isOwn ? 'text-blue-100 hover:text-white' : 'text-gray-500 hover:text-gray-700'
              }`}
              title="Add reaction"
            >
              <FiSmile className="w-4 h-4" />
            </button>
            {showReactions && (
              <div className="absolute bottom-8 right-0 bg-white border rounded-lg shadow-xl p-2 grid grid-cols-4 gap-1 z-50">
                {quickReactions.map((emoji) => (
                  <button
                    key={emoji}
                    onClick={() => handleReact(emoji)}
                    className="p-2 hover:bg-gray-100 rounded-lg text-lg transition-all duration-200 flex items-center justify-center min-h-8 min-w-8"
                    title={`React with ${emoji}`}
                  >
                    <span className="emoji">{emoji}</span>
                  </button>
                ))}
              </div>
            )}
          </div>
        </div>
        <MessageReactions 
          reactions={message.reactions} 
          onReact={onReact} 
          messageId={message.id}
        />
      </div>
    </div>
  );
};

export default ChatApp;
